(function () {

    "use strict";

    var fluid = require("infusion");
    var gpii = fluid.registerNamespace("gpii");

    fluid.registerNamespace("gpii.request.flowManager");

    fluid.defaults("gpii.flowManager.browserChannel", {
        gradeNames: ["fluid.standardRelayComponent", "autoInit"]
    });

    fluid.defaults("kettle.requests.request.handler.browserChannel", {
        gradeNames: ["gpii.request.flowManager.sessionAware", "autoInit"],
        invokers: {
            handle: {
                funcName: "gpii.request.flowManager.browserChannelHandle",
                args: ["{request}", "{requestProxy}.events.onSuccess"],
                dynamic: true
            }
        }
    });

    gpii.request.flowManager.browserChannelHandle = function (fluidRequest, event) {
        var data = fluidRequest.data;

        var initialSettings = gpii.settingsHandlers.WebSockets.getSettingsForId(data);
        fluidRequest.socket.emit("connectionSucceeded", initialSettings);
        gpii.settingsHandlers.WebSockets.addClient(data, fluidRequest.socket);

        fluidRequest.socket.on("disconnect", function () {
            gpii.settingsHandlers.WebSockets.removeClient(fluidRequest.socket);
        });

        gpii.request.flowManager.logAndNotify("Lifecycle manager returned: ", event)("A request to browserChannel has been successfully processed");
    };

})();
