(function () {

    "use strict";
   
    var express = require("express"),
        fluid = require("infusion"),
        path = require("path"),
        gpii = fluid.registerNamespace("gpii");

    fluid.require("gpiiFramework", require);
    fluid.require("./UserLogin.js", require);
    fluid.require("./UserLogout.js", require);

    fluid.require("lifecycleManager", require);

    fluid.defaults("gpii.flowManager", {
        gradeNames: ["gpii.app", "autoInit"],
        urls: {
            preferences: "",
            deviceReporter: "",
            matchMaker: "",
            solutionsReporter: ""
        },
        components: {
            rawPreferencesDataSource: {
                type: "gpii.dataSource",
                options: {
                    url: "{gpii.flowManager}.options.urls.preferences",
                    termMap: {
                        token: "%token"
                    }
                }
            },
            preferencesDataSource: {
                type: "gpii.requestContextDataSource"
            },
            rawDeviceReporterDataSource: {
                type: "gpii.dataSource",
                options: {
                    url: "{gpii.flowManager}.options.urls.deviceReporter"
                }
            },
            deviceReporterDataSource: {
                type: "gpii.requestContextDataSource"
            },
            rawMatchMakerDataSource: {
                type: "gpii.dataSource",
                options: {
                    url: "{gpii.flowManager}.options.urls.matchMaker",
                    writable: true
                }
            },
            matchMakerDataSource: {
                type: "gpii.requestContextDataSource"
            },
            lifecycleManager: {
                type: "gpii.lifecycleManager"
            }
        },
        handlers: {
            userLogin: {
                route: "/user/:token/login",
                type: "get"
            },
            userLogout: {
                route: "/user/:token/logout",
                type: "get"
            }
        }
    });

    fluid.demands("gpii.urlExpander", ["flowManager.development", "gpii.flowManager"], {
        options: {
            vars: {
                port: "{gpii.server}.options.port",
                root: path.join(__dirname, "..")
            }
        }
    });

    fluid.demands("rawPreferencesDataSource", "gpii.flowManager", {
        funcName: "gpii.dataSource.URL"
    });
    fluid.demands("preferencesDataSource", "gpii.flowManager", {
        options: {
            components: {
                rawSource: "{rawPreferencesDataSource}"
            }
        }
    });

    fluid.demands("rawDeviceReporterDataSource", "gpii.flowManager", {
        funcName: "gpii.dataSource.URL"
    });
    fluid.demands("deviceReporterDataSource", "gpii.flowManager", {
        options: {
            components: {
                rawSource: "{rawDeviceReporterDataSource}"
            }
        }
    });

    fluid.demands("rawMatchMakerDataSource", "gpii.flowManager", {
        funcName: "gpii.dataSource.URL"
    });
    fluid.demands("matchMakerDataSource", "gpii.flowManager", {
        options: {
            components: {
                rawSource: "{rawMatchMakerDataSource}"
            }
        }
    });

})();