(function () {

    "use strict";

    var fluid = require("infusion"),
        when = require("when"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "matchPost", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.matchPost",
                    args: ["{requestProxy}", "{request}.req.body", "{gpii.matchMaker}"]
                }
            }
        }
    });

    gpii.requests.request.handler.matchPost = function (requestProxy, model, matchMaker) {
        var solutionsReporter = matchMaker.solutionsReporter,
            transform = matchMaker.transformer.transformSettings,
            os = fluid.get(model, "device.OS.id"),
            version = fluid.get(model, "device.OS.version");

        solutionsReporter.get({
            os: os,
            version: version
        }, function (solutions) {
            if (!solutions) {
                requestProxy.events.onError.fire();
                return;
            }
            matchMaker.prefilterSolutions(solutions, os, version);
            solutions = matchMaker.prefilterSolutions(solutions, model.device);

            var transformedSolutions = fluid.transform(matchMaker.options.strategies, function (thisStrategy) {
                var strategy = fluid.getGlobalValue(thisStrategy.strategy);
                return when(transform({
                    solutions: matchMaker.match(model.preferences, solutions, strategy),
                    preferences: model.preferences
                }));
            });

            when.all(transformedSolutions, function () {
                // TODO: Deal with transformed solutions from multiple strategies.
                requestProxy.events.onSuccess.fire();
            });
        });
    };

})();