(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "matchPut", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.matchPut",
                    args: ["{request}", "{gpii.matchMaker}"]
                }
            }
        }
    });

    gpii.requests.request.handler.matchPut = function (request, matchMaker) {
        var req = request.req,
            res = request.res,
            model = req.body,
            solutionsReporter = matchMaker.solutionsReporter,
            transform = matchMaker.transformer.transformSettings,
            os = fluid.get(model, "device.OS.id"),
            version = fluid.get(model, "device.OS.version");

        var strategy = fluid.getGlobalValue(matchMaker.options.strategy);
        solutionsReporter.get({
            os: os,
            version: version
        }, function (solutions) {
            if (!solutions) {
                res.send({
                    isError: true,
                    message: "Unknown error"
                }, 500);
                return;
            }
            if (solutions.isError) {
                res.send(solutions, 500);
                return;
            }
            matchMaker.prefilterSolutions(solutions, os, version);
            solutions = matchMaker.prefilterSolutions(solutions, model.device);
            res.send(transform({
                solutions: matchMaker.match(model.preferences, solutions, strategy),
                preferences: model.preferences
            }), 200);
        });
    };

})();