/* global require */
"use strict";

var fluid = fluid || require("infusion"),
    utils = fluid.registerNamespace("gpii.ontologyHandler.utils"),
    transforms = fluid.registerNamespace("gpii.ontologyHandler.transforms"),
    $ = fluid.registerNamespace("jQuery");

utils.expandTransformations = function (transformations) {
    var expanded = {};
    fluid.each(transformations, function (transformation, elPath) {
        var source, sourceURI;
        if (typeof transformation !== "string" || transformation.charAt(0) !== "{") {
            expanded[elPath] = transformation;
            return;
        }
        source = fluid.parseContextReference(transformation, 0);
        sourceURI = fluid.pathUtil.escapeSegment(source.context);
        expanded[elPath] = fluid.model.composeSegments(sourceURI, source.path);
    });
    return expanded;
};

utils.filter = function (full, remove) {
    var modified = false;
    var ret = ($.isArray(full)) ? [] : {};
    for (var ind in full) {
        if (ind in remove) {
            if (fluid.isPrimitive(remove[ind]) || $.isArray(remove[ind])) {
                continue;
            } else {
                var res = utils.filter(full[ind], remove[ind]);
                if (res === undefined) {
                    continue;
                }

                ret[ind] = res;
                modified = true;
            }
        } else {
            ret[ind] = full[ind];
            modified = true;
        }
    }
    return modified ? ret : undefined;
};

utils.filterPrefs = function (fullSet, toRemove) {
    var cpy = fluid.copy(fullSet);
    var result = utils.filter(cpy, toRemove);
    console.log(JSON.stringify(result));
    return result;
};

//Transforms required for application specific settings
fluid.defaults("gpii.ontologyHandler.transforms.applicationISOToFlat", {
    gradeNames: "fluid.standardTransformFunction",
    invertConfiguration: "gpii.ontologyHandler.transforms.applicationISOToFlat.invert"
});

transforms.applicationISOToFlat = function (preferences, transformSpec, transform) {
    var value, key;
    fluid.each(preferences, function (data, appId) {
        value = [{ "value": data.parameters }];
        key = "http://registry\\.gpii\\.net/applications/" + fluid.pathUtil.escapeSegment(appId);
        fluid.model.transform.setValue(key, value, transform);
    });
};

transforms.applicationISOToFlat.invert = function (transformSpec) {
    var togo = fluid.copy(transformSpec);
    togo.inputPath = transformSpec.outputPath;
    togo.outputPath = transformSpec.inputPath;
    togo.type = "gpii.ontologyHandler.transforms.applicationFlatToISO";
    return togo;
};

fluid.defaults("gpii.ontologyHandler.transforms.applicationFlatToISO", {
    gradeNames: "fluid.standardTransformFunction"
});

transforms.applicationFlatToISO = function (preferences, transformSpec, transform) {
    var searchValue = "//registry.gpii.net/applications/";
    fluid.each(preferences, function (preference, uri) {
        var appIndex = uri.indexOf(searchValue),
            appId;
        if (appIndex < 0) {
            return;
        }
        appId = uri.substring(appIndex + searchValue.length);
        preference = {
            parameters: preference[0].value
        };
        fluid.model.transform.setValue(fluid.model.composeSegments(transformSpec.outputPath,
            fluid.pathUtil.escapeSegment(appId)), preference, transform);
    });
};