(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "ontologyPreferencesGet", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.ontologyPreferencesGet",
                    args: ["{requestProxy}", "{request}.req.params.to", "{request}.req.params.preferences", "{ontologyServer}.ontologySource"]
                }
            }
        }
    });

    gpii.requests.request.handler.ontologyPreferencesGet = function (requestProxy, transformOntologyTo, preferences, ontologySource) {
        if (transformOntologyTo !== "default") {
            requestProxy.events.onError.fire({
                isError: true,
                message: "Ontology server currently support only transformations to default ontology."
            });
            return;
        }
        var ontologyName = gpii.ontologyServer.getOntologyName(preferences);
        ontologySource.get({
            name: ontologyName
        }, function (ontologyData) {
            requestProxy.events.onSuccess.fire(fluid.model.transformWithRules(preferences, ontologyData.transformations));
        });
    };

})();