(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "ontologyPreferencesGet", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.ontologyPreferencesGet",
                    args: ["{that}", "{requestProxy}", "{request}.req.params", "{ontologyServer}.ontologySource"]
                }
            }
        }
    });

    gpii.requests.request.handler.ontologyPreferencesGet = function (that, requestProxy, model, ontologySource) {
        if (model.to !== "default") {
            requestProxy.events.onError.fire({
                isError: true,
                message: "Ontology server currently support only transformations to default ontology."
            });
            return;
        }
        var preferences = model.preferences,
            ontologyName = gpii.ontologyServer.getOntologyName(preferences);
        that.when(ontologySource.get({
            name: ontologyName
        }), function (transformations) {
            var transformed = fluid.model.transformWithRules(preferences, transformations);
            return transformed;
        });
    };

})();