/* global require */
(function () {
    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.registerNamespace("gpii.ontology.post");

    fluid.defaults("kettle.requests.request.handler.ontologyPost", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        invokers: {
            handle: {
                funcName: "gpii.ontology.post.request",
                args: [ "{request}", "{ontologyServer}", "{that}.events.onRawPreferences"],
                dynamic: true
            },
            postHandler: {
                funcName: "gpii.ontology.post.postHandler",
                args: [ "{arguments}.0", "{request}", "{requestProxy}" ]
            }
        },
        events: {
            onRawPreferences: null
        },
        listeners: {
            onRawPreferences: "{that}.postHandler"
        }
    });

    gpii.ontology.post.request = function (request, ontologyServer, event) {
        //key preferences set by ontology
        var view = (request.req.query && request.req.query.view) ? request.req.query.view : "flat";
        var payload = {};
        payload[view] = request.req.body;

        ontologyServer.setRawPreferences(event, payload, undefined, "POST");
    };

    gpii.ontology.post.postHandler = function (reply, request, requestProxy) {
        var ret = {
            token: reply.token,
            preferences: reply.preferences[request.req.query.view ? request.req.query.view : "flat"]
        };
        requestProxy.events.onSuccess.fire(ret);
    };
})();