/* global require */
//TODO KASPER: make sure that it works with putting to a non-existing NP set
(function () {
    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.registerNamespace("gpii.preferencesFramework.put");

    fluid.defaults("kettle.requests.request.handler.preferencesPut", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        invokers: {
            handle: {
                funcName: "gpii.preferencesFramework.put.request",
                args: [ "{request}", "{preferencesFramework}", "{that}.events.onGetRawPreferences"],
                dynamic: true
            },
            onGetRawPreferences: {
                funcName: "gpii.preferencesFramework.put.onGetRawPreferences",
                args: [ "{preferencesFramework}", "{preferencesFramework}.ontologyHandler", 
                     "{request}", "{arguments}.0", "{that}.events.onPutRawPreferences" ]
            },
            onPutRawPreferences: {
                funcName: "gpii.preferencesFramework.put.onPutRawPreferences",
                args: [ "{arguments}.0", "{request}", "{requestProxy}" ]
            }
        },
        events: {
            onGetRawPreferences: null,
            onPutRawPreferences: null
        },
        listeners: {
            onGetRawPreferences: "{that}.onGetRawPreferences",
            onPutRawPreferences: "{that}.onPutRawPreferences"
        }
    });

    gpii.preferencesFramework.put.request = function (request, preferencesFramework, event) {
        preferencesFramework.getRawPreferences(event, request.req.params.token);
    };

    gpii.preferencesFramework.put.onGetRawPreferences = function (preferencesFramework, ontologyHandler, request, rawPrefs, event) {
        //get the view of the given preferences (in body), update raw prefernces set and PUT it
        var prefsView = gpii.preferencesFramework.getRequestView(request);
        rawPrefs = ontologyHandler.addPrefsToRawPrefs(request.req.body, prefsView, rawPrefs);
        preferencesFramework.setRawPreferences(event, rawPrefs, request.req.params.token);
    };

    gpii.preferencesFramework.put.onPutRawPreferences = function (reply, request, requestProxy) {
        //despite potentially having modified the entire raw preferences set, we only return the
        //preferences in the view that we originally got them
        var ret = {
            token: reply.token,
            preferences: reply.preferences[gpii.preferencesFramework.getRequestView(request)]
        };
        requestProxy.events.onSuccess.fire(ret);
    };
})();