(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "userGet", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.userGet",
                    args: ["{requests}", "{request}", "{preferencesServer}.userSource"]
                }
            }
        }
    });

    gpii.requests.request.handler.userGet = function (requests, request, userSource) {
        userSource.get({
            token: request.req.params.token
        }, gpii.callbackWrapper(request, function (resp) {
            requests.events.onSuccess.fire(resp);
        }));
    };

})();