(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "userGet", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.userGet",
                    args: ["{requestProxy}", "{request}.req.params.token", "{preferencesServer}.userSource"]
                }
            }
        }
    });

    gpii.requests.request.handler.userGet = function (requestProxy, token, userSource) {
        userSource.get({
            token: token
        }, requestProxy.events.onSuccess.fire);
    };

})();