(function () {

    "use strict";

    var fluid = require("infusion"),
        uuid = require("node-uuid"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "userPost", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.userPost",
                    args: ["{requestProxy}", "{request}.req", "{preferencesServer}.userSource"]
                }
            }
        }
    });

    gpii.requests.request.handler.userPost = function (requestProxy, req, userSource) {
        userSource.set({
            token: req.params.token || uuid.v4()
        }, req.body, function (resp) {
            if (resp.ok) {
                resp = fluid.model.transformWithRules(resp, {
                    "_rev": "rev",
                    "_id": "id"
                });
            }
            requestProxy.events.onSuccess.fire(resp);
        });
    };

})();