(function () {

    "use strict";

    var fluid = require("infusion"),
        uuid = require("node-uuid"),
        gpii = fluid.registerNamespace("gpii"),
        kettle = fluid.require("kettle", require);

    fluid.defaults("kettle.requests.request.handler.userPut", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        invokers: {
            handle: {
                funcName: "gpii.handleUserPut",
                args: ["{requestProxy}", "{request}.req", "{rawPreferencesServer}.userSource"],
                dynamic: true
            }
        }
    });

    gpii.handleUserPut = function (requestProxy, req, userSource) {
        var token = req.params.token;
        userSource.set({
            token: token
        }, req.body, function (preferences) {
            gpii.rawPreferencesServer.handlePreferences(requestProxy, token, preferences);
        });
    };

})();
