(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "solutionGet", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.solutionGet",
                    args: ["{requests}", "{request}", "{solutionsRegistry}.solutionSource"]
                }
            }
        }
    });

    gpii.requests.request.handler.solutionGet = function (requests, request, solutionSource) {
        solutionSource.get({
            id: request.req.params.id
        }, gpii.callbackWrapper(request, function (resp) {
            requests.events.onSuccess.fire(resp);
        }));
    };

})();