(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "solutionQuery", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.solutionQuery",
                    args: ["{request}", "{solutionsRegistry}.solutionQuerySource"]
                }
            }
        }
    });

    gpii.requests.request.handler.solutionQuery = function (request, solutionQuerySource) {
        var req = request.req,
            callback = gpii.requests.request.handler.makeQueryCallback(req, request.res);
        if (!req.query.os) {
            callback({
                isError: true,
                message: "No os specified."
            });
            return;
        }
        solutionQuerySource.get({
            os: req.query.os
        }, callback);
    };

    gpii.requests.request.handler.makeQueryCallback = function (req, res) {
        return function (resp) {
            if (!resp) {
                res.send({
                    isError: true,
                    message: "Unknown error"
                }, 500);
                return;
            }
            if (resp.isError) {
                res.send(resp, 500);
                return;
            }
            res.send(resp, 200);
        };
    };

})();