(function () {

    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "solutionQuery", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.solutionQuery",
                    args: ["{requests}", "{request}", "{solutionsRegistry}.solutionQuerySource"]
                }
            }
        }
    });

    gpii.requests.request.handler.solutionQuery = function (requests, request, solutionQuerySource) {
        var req = request.req;
        if (!req.query.os) {
            requests.events.onError.fire({
                isError: true,
                message: "No os specified."
            });
            return;
        }
        solutionQuerySource.get({
            os: req.query.os
        }, gpii.callbackWrapper(request, function (resp) {
            requests.events.onSuccess.fire(resp);
        }));
    };

})();