(function () {

    "use strict";

    var fluid = require("infusion"),
        semver = require("semver"),
        gpii = fluid.registerNamespace("gpii");

    fluid.demands("gpii.requests.request.handler", "solutionQuery", {
        options: {
            invokers: {
                handle: {
                    funcName: "gpii.requests.request.handler.solutionQuery",
                    args: ["{request}", "{solutionsRegistry}.solutionQuerySource"]
                }
            }
        }
    });

    gpii.requests.request.handler.solutionQuery = function (request, solutionQuerySource) {
        var req = request.req,
            callback = gpii.requests.request.handler.makeQueryCallback(req, request.res);
        if (!req.query.os) {
            callback({
                isError: true,
                message: "No os specified."
            });
            return;
        }
        solutionQuerySource.get({
            os: req.query.os
        }, callback);
    };

    gpii.requests.request.handler.solutionQuery.filterByVersion = function (solutions, os, version) {
        if (!version) {
            return;
        }
        fluid.remove_if(solutions, function (solution) {
            var solutionOSVersion = fluid.find(fluid.get(solution, "contexts.OS"), function(OSEntry) {
                return OSEntry.id === os ? OSEntry.version : undefined;
            });
            if (!semver.satisfies(version, solutionOSVersion)) {
                return true;
            }
        });
    };

    gpii.requests.request.handler.makeQueryCallback = function (req, res) {
        return function (resp) {
            if (!resp) {
                res.send({
                    isError: true,
                    message: "Unknown error"
                }, 500);
                return;
            }
            if (resp.isError) {
                res.send(resp, 500);
                return;
            }
            gpii.requests.request.handler.solutionQuery.filterByVersion(resp, req.query.os, req.query.version);
            res.send(resp, 200);
        };
    };

})();